import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { handleError, handleSuccess } from '../utils';

function Signup() {
    // 1. Add a loading state for better user experience
    const [loading, setLoading] = useState(false);
    const [signupInfo, setSignupInfo] = useState({
        name: '',
        email: '',
        password: ''
    });

    const navigate = useNavigate();

    // 2. Simplify the state update logic
    const handleChange = (e) => {
        const { name, value } = e.target;
        setSignupInfo(prev => ({ ...prev, [name]: value }));
    };

    const handleSignup = async (e) => {
        e.preventDefault();
        const { name, email, password } = signupInfo;
        if (!name || !email || !password) {
            return handleError('Name, email, and password are required');
        }
        setLoading(true); // Disable form while request is pending
        try {
            // 3. Use environment variables for the API URL
            const url = `${process.env.REACT_APP_BASE_URL}/auth/signup`;
            const response = await fetch(url, {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(signupInfo)
            });

            const result = await response.json();

            // 4. Streamline response handling
            if (response.ok) { // Check for successful HTTP status (200-299)
                handleSuccess(result.message);
                setTimeout(() => navigate('/login'), 1000);
            } else {
                // Handle backend validation errors or other issues
                const errorMessage = result.message || (result.error?.details && result.error.details[0].message);
                handleError(errorMessage || 'An unexpected error occurred');
            }
        } catch (err) {
            handleError('Network error, please try again later.');
        } finally {
            setLoading(false); // Re-enable form
        }
    };

    return (
        <div className='container'>
            <h1>Signup</h1>
            <form onSubmit={handleSignup}>
                <div>
                    <label htmlFor='name'>Name</label>
                    <input
                        onChange={handleChange}
                        type='text'
                        name='name'
                        autoFocus
                        placeholder='Enter your name...'
                        value={signupInfo.name}
                        disabled={loading}
                    />
                </div>
                <div>
                    <label htmlFor='email'>Email</label>
                    <input
                        onChange={handleChange}
                        type='email'
                        name='email'
                        placeholder='Enter your email...'
                        value={signupInfo.email}
                        disabled={loading}
                    />
                </div>
                <div>
                    <label htmlFor='password'>Password</label>
                    <input
                        onChange={handleChange}
                        type='password'
                        name='password'
                        placeholder='Enter your password...'
                        value={signupInfo.password}
                        disabled={loading}
                    />
                </div>
                <button type='submit' disabled={loading}>
                    {loading ? 'Signing up...' : 'Signup'}
                </button>
                <span>Already have an account?
                    <Link to="/login">Login</Link>
                </span>
            </form>
            <ToastContainer />
        </div>
    );
}

export default Signup;
